import React, { Component } from 'react';
import Web3 from 'web3'
import icon from '../todo.png';
import TodoList from '../abis/TodoList.json'
import './App.css';

class App extends Component {

  async componentDidMount() {
    await this.loadWeb3()
    // await this.loadBlockchainData()
    console.log(window.web3)
  }

  async loadWeb3() {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else {
      window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
    }
    console.log(window.web3)
  }

  render() {
    return (
      <div>

        <nav className="navbar navbar-dark fixed-top bg-dark flex-md-nowrap p-0 shadow">
          <a
            className="navbar-brand col-sm-3 col-md-2 mr-0"
            href="http://google.com/"
            target="_blank"
            rel="noopener noreferrer"
          >
            My Todo List
          </a>

          <div>
                <ul className="navbar-nav px-3">
                    <li className="nav-item flex-nowrap d-none d-sm-none d-sm-block">
                        <small className="navbar-text">
                            {/* Your account: {this.state.account} */}
                        </small>
                    </li>
                </ul>

            </div>
        </nav>


        <div className="container-fluid mt-5">
          <div className="row">
            <main role="main" className="col-lg-12 d-flex text-center">
              <div className="content mr-auto ml-auto">
                <a
                  href="http://google.com"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  <img src={icon} className="App-logo" alt="logo" width="200"/>
                </a>
                <h1>My Todo List</h1>

              </div>
            </main>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
